import React, { useState } from 'react';
import Rule from './RuleUI'; // Pastikan path ini sesuai dengan lokasi file komponen Rule

const rulesData = [
    { text: 'Your password must be at least 5 characters.', passed: false },
    { text: 'Your password must include a number.', passed: false },
    { text: 'Your password must include an uppercase letter.', passed: false },
    // Tambahkan aturan lainnya di sini
];

const RulesList = () => {
    const [rules, setRules] = useState(rulesData);

    return (
        <div style={{ width: '400px', margin: '0 auto' }}>
            <h1>The Password Game</h1>
            <p>Please choose a password</p>
            <input type="text" style={{ width: '100%', padding: '10px', fontSize: '16px', marginBottom: '20px' }} />
            {rules.map((rule, index) => (
                <Rule key={index} index={index + 1} text={rule.text} passed={rule.passed} />
            ))}
        </div>
    );
};

export default RulesList;
