import React from 'react';

const Rule = ({ index, text, passed }) => {
    return (
        <div style={{
            border: `1px solid ${passed ? 'green' : 'red'}`,
            backgroundColor: `${passed ? '#e6ffed' : '#ffe6e6'}`,
            padding: '10px',
            margin: '5px 0',
            borderRadius: '10px',
            display: 'flex',
            alignItems: 'center',
            boxShadow: '0 1px 3px rgba(0, 0, 0, 0.12), 0 1px 2px rgba(0, 0, 0, 0.24)'
        }}>
            <div style={{ 
                display: 'flex', 
                alignItems: 'center',
                marginRight: '10px'
            }}>
                <span style={{ 
                    fontSize: '20px', 
                    color: `${passed ? 'green' : 'red'}`,
                    marginRight: '10px'
                }}>
                    {passed ? '✓' : '✗'}
                </span>
                <strong style={{ 
                    fontSize: '16px',
                    color: 'black'
                }}>Rule {index}</strong>
            </div>
            <div style={{ 
                display: 'flex', 
                flexDirection: 'column'
            }}>
                <p style={{
                    margin: 0,
                    fontSize: '14px',
                    color: 'black'
                }}>text}</p>
            </div>
        </div>
    );
};

export default Rule;
